import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './datePicker.css';

function DatePickerPage() {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');

  const handleDateChange = (dates) => {
    const [start, end] = dates;
    setStartDate(start);
    setEndDate(end);
  };

  const handleStartTimeChange = (e) => {
    setStartTime(e.target.value);
  };

  const handleEndTimeChange = (e) => {
    setEndTime(e.target.value);
  };

  const handleSubmit = () => {
    // A back-end későbbi csatlakoztatása céljából előkészített adatobjektum
    const data = {
      startDate,
      endDate,
      startTime,
      endTime,
    };
    
    // Egyelőre csak a konzolra írjuk ki az adatokat
    console.log('Data to submit:', data);

    // Amikor a back-end elkészül, itt egy fetch/axios hívás fog elindulni
    // példa:
    // fetch('http://your-backend-endpoint.com/api/save-dates', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(data),
    // })
    //   .then(response => response.json())
    //   .then(data => console.log(data))
    //   .catch(error => console.error('Error:', error));
  };

  return (
    <div className="grid-container">
      <div className="left-panel">
        <button onClick={handleSubmit}>Submit Data</button>
      </div>
      <div className="right-panel">
        <h1>Book parking details</h1>
        <h2>Select date</h2>
        <div className="calendar-container">
          <DatePicker
            selected={startDate}
            onChange={handleDateChange}
            startDate={startDate}
            endDate={endDate}
            selectsRange
            inline
            dateFormat="yyyy/MM/dd"
          />
        </div>

        {startDate && endDate && (
          <div className="time-selection">
            <label>
              Start Time:
              <input
                type="time"
                value={startTime}
                onChange={handleStartTimeChange}
              />
            </label>

            <label>
              End Time:
              <input
                type="time"
                value={endTime}
                onChange={handleEndTimeChange}
              />
            </label>
          </div>
        )}
      </div>
    </div>
  );
}

export default DatePickerPage;
